import React from 'https://esm.sh/react@17.0.2';
import ReactDOM from 'https://esm.sh/react-dom@17.0.2';
import Container from 'https://esm.sh/@material-ui/core@4.12.4/Container?deps=react@17.0.2';
import List from 'https://esm.sh/@material-ui/core@4.12.4/List?deps=react@17.0.2';
import ListItem from 'https://esm.sh/@material-ui/core@4.12.4/ListItem?deps=react@17.0.2';
import ListItemText from 'https://esm.sh/@material-ui/core@4.12.4/ListItemText?deps=react@17.0.2';
import Checkbox from 'https://esm.sh/@material-ui/core@4.12.4/Checkbox?deps=react@17.0.2';
import ExpandLess from 'https://esm.sh/@material-ui/icons@4.11.2/ExpandLess?deps=react@17.0.2';
import ExpandMore from 'https://esm.sh/@material-ui/icons@4.11.2/ExpandMore?deps=react@17.0.2';

const NestedListItem = ({ data, updateData, item, runQuery }) => {
  const [open, setOpen] = React.useState(true);
  const [checked, setChecked] = React.useState(false);

  //  initialize checkbox state from global storage
  React.useEffect(() => {
    if (item.label) {
      const saved = data.checkedStatus?.[item.label] || false;
      setChecked(saved);
    }
  }, [item.label]);

  const handleCheck = (event) => {
    const newValue = event.target.checked;
    setChecked(newValue);

    if (item.label) {
      runQuery("restapi1", { query: newValue ? item.query : "", checked: newValue });
       runQuery("runjs5", { label: item.label, setted: newValue });
    }
  };

  return (
    <>
      <ListItem style={{ padding: item.label ? "0px" : "5px 15px", textTransform: item.label ? "none" : "capitalize"}} button={!item.label} onClick={() => item.values && setOpen(!open)}>
        {item.label && (
          <Checkbox
            checked={checked}
            onChange={handleCheck}
            color="primary"
            onClick={(e) => e.stopPropagation()} // prevent expanding when clicking checkbox
          />
        )}
        <ListItemText style={{ wordBreak: "break-word"}} primary={item.label || item.name} />
        {item.values ? (open ? <ExpandLess /> : <ExpandMore />) : null}
      </ListItem>

      {item.values && open && (
        <List component="div" disablePadding style={{ paddingLeft: 20 }}>
          {item.values.map((child, idx) => (
            <NestedListItem runQuery={runQuery} key={idx} item={child} data={data} updateData={updateData} />
          ))}
        </List>
      )}
    </>
  );
};

const MyCustomComponent = ({ data, updateData, runQuery }) => {
  const facets = data.facets || [];

  return (
    <Container style={{ padding: "0px", fontFamily: "IBM Plex Sans"}}>

      <h2 style={{ padding: "15px 10px", margin: "0px", background: "#FFF", zIndex: "1", position: "sticky", top: "0", borderBottom: "1px solid #CCD1D5"}}>Facets</h2>
      <List>
        {facets.map((facet, idx) => (
          <NestedListItem runQuery={runQuery} key={idx} item={facet} data={data} updateData={updateData} />
        ))}
      </List>
    </Container>
  );
};

const ConnectedComponent = Tooljet.connectComponent(MyCustomComponent);
ReactDOM.render(<ConnectedComponent />, document.body);

